import React, { useState, useEffect } from 'react';
import DatePicker from 'react-datepicker';
import '../../styles//QuestionnaireForm.css';
import './inner-component/react-datepicker'




function MultiPageForm() {
  const [formData, setFormData] = useState({
    TourArea: '',
    TourCount: '',

    textInput: '',
    TourTimeMorning: false,
    TourTimeAfternoon: false,
    TourTimeEvening: false,

    selectedDate: null,

    radioInput: '',
    rangeInput: '',
    textareaInput: '',
    textInput2: '',
  });

  const [currentPage, setCurrentPage] = useState(0);
  const [totalPages, setTotalPages] = useState(3);
  const storedData = JSON.parse(localStorage.getItem('formData'));
  const email = localStorage.getItem('email');
  const selectedDay = formData.selectedDate ? formData.selectedDate.toLocaleDateString('he-IL', { weekday: 'long' }) : '';
  // const selectedDayText = `היום שנבחר הוא ${selectedDay}`;
  const [showOptions, setShowOptions] = useState(false);
  const [selectedOption, setSelectedOption] = useState("");


  useEffect(() => {
    // Save form data to local storage whenever formData changes
    localStorage.setItem('formData', JSON.stringify(formData));
  }, [formData]);


  useEffect(() => {
    // Load form data from local storage when component mounts
    const storedData = localStorage.getItem('formData');
    if (storedData) {
      setFormData(JSON.parse(storedData));
    }
  }, []);
  

  const handleInputChange = (event) => {
    const { name, value, type, checked } = event.target;
    const newValue = type === 'checkbox' ? checked : value;
    setFormData({
      ...formData,
      [name]: newValue,
    });
    if (name === 'selectedDate') { // Add this condition to update localStorage for date changes
      localStorage.setItem('formData', JSON.stringify({ ...formData, [name]: newValue }));
      document.querySelector('.react-datepicker__close-icon').click();
    }
  };

  
  const handleFormSubmit = (event) => {
    console.log('currentPage', currentPage);
    event.preventDefault();
    // Submit form data to server or do other final processing
    // ...
    alert('Form submitted successfully!');
  };


  const handleCheckboxChange = (event) => {
    setShowOptions(event.target.checked);
  };

  const handleOptionChange = (event) => {
    setSelectedOption(event.target.value);
  };


  // ********** Page0 **********
  const renderPage0 = () => (
    <div>
      <h1 style={{textAlign: 'center', color: 'rgba(0, 0, 0, 0)'}}>-------------------------------------------------</h1>
      <h1 style={{textAlign: 'center'}}>Welcome</h1> 
      <h2 style={{textAlign: 'center'}}>{email}</h2>
      {/* <p>{JSON.stringify(localStorage)}</p> */}

      <br />
      <br />

    </div>

  );


// ********** Page1 **********
  const renderPage1 = () => (
    <div>
      <h1 style={{textAlign: 'center', color: 'rgba(0, 0, 0, 0)'}}>-------------------------------------------------</h1>
      <h4 style={{textAlign: 'center'}}>Page 1 of {totalPages}</h4>

      <label>
        :בחרו איזור לסיור היין שלכם
        <br /><select 
          name="TourArea"
          value={formData.TourArea}
          onChange={handleInputChange}
        >
          <option value="">יש לבחור מהרשימה</option>
          <option value="Jerusalem">ירושלים</option>
          <option value="Center">מרכז</option>
          <option value="North">צפון</option>
        </select>
      </label>

      <br />
      <br />

      <label>
        :בחרו תאריך לסיור היין שלכם
        <br />

        <DatePicker
          selected={formData.selectedDate}
          onChange={(date) => setFormData({...formData, selectedDate: date})}
          placeholderText="יש לבחור תאריך"
          dateFormat="dd/MM/yyyy"
        />


          {formData.selectedDate && (
          <p>{`היום שנבחר הוא ${selectedDay}`}</p>
          )}
      </label>

    


      <br />
      <br />

      <label>
        :בכמה יקבים תרצו לסייר בסיור היין שלכם
        <br /><select 
          name="TourCount"
          value={formData.TourCount}
          onChange={handleInputChange}
        >
          <option value="">יש לבחור מהרשימה</option>
          <option value="1">1</option>
          <option value="2">2</option>
          <option value="3">3</option>
        </select>
      </label>

      <br />
      <br />

      <label>
        <ul class="form-data-list">:מהן שעות הסיור הרצויות עבורכם
        <br />
        <br />

        <li>בוקר<input
          className="form-checkbox-input"
          type="checkbox"
          name="TourTimeMorning"
          checked={formData.checkboxInput}
          onChange={handleInputChange}
        />
        </li>

        
        <li>צהריים<input
          className="form-checkbox-input"
          type="checkbox"
          name="TourTimeAfternoon"
          checked={formData.checkboxInput}
          onChange={handleInputChange}
        />
        </li>

        
        <li>ערב<input
          className="form-checkbox-input"
          type="checkbox"
          name="TourTimeEvening"
          checked={formData.checkboxInput}
          onChange={handleInputChange}
        />
        </li>
        </ul>
      </label>

      <br />


    </div>
  );




  // ********** Page2 **********
  const renderPage2 = () => (
    <div >
      <h1 style={{textAlign: 'center', color: 'rgba(0, 0, 0, 0)'}}>-------------------------------------------------</h1>
      <h4 style={{textAlign: 'center'}}>Page 2 of {totalPages}</h4>

      <label>
        :מספר אנשים בסיור
        <br /><select 
          name="TourPeopleCount"
          value={formData.TourPeopleCount}
          onChange={handleInputChange}
        >
          <option value="">יש לבחור מהרשימה</option>
          <option value="1-2">1-2</option>
          <option value="2-5">2-4</option>
          <option value="5+">5+</option>
        </select>
      </label>

      <br />
      <br />

      <label>
        :תקציב רצוי לאדם
        <br /><input
          type="range"
          name="rangeInput"
          min="0"
          max="100"
          value={formData.rangeInput}
          onChange={handleInputChange}
        />
        <span className="form-input-container">{formData.rangeInput}</span>
      </label>

      <br />
      <br />

      <div>
      <label>
        <input type="checkbox" onChange={handleCheckboxChange} />
        Show additional options
      </label>

      {showOptions && (
        <div>
          <label>
            Select an option:
            <select value={selectedOption} onChange={handleOptionChange}>
              <option value="">Select an option</option>
              <option value="option1">Option 1</option>
              <option value="option2">Option 2</option>
              <option value="option3">Option 3</option>
            </select>
          </label>
        </div>
      )}
    </div>

      <label className="form-input-container">
        Free Text Input:
        <textarea
          className="form-input-container"
          name="textareaInput"
          value={formData.textareaInput}
          onChange={handleInputChange}
        />
      </label>

      <br />
      <br />

      <label className="form-input-container">
        Text Input 2:
        <input
          className="form-input-container"
          type="text"
          name="textInput2"
          value={formData.textInput2}
          onChange={handleInputChange}
        />
      </label>

      <br />
      <br />

    </div>
  );


  // ********** Page3 **********

  const renderPage3 = () => (
    <div>
      <h1 style={{textAlign: 'center', color: 'rgba(0, 0, 0, 0)'}}>-------------------------------------------------</h1>
      <h4 style={{textAlign: 'center'}}>Page 3 of {totalPages}</h4>
      <p>Summary of form data:</p>


      <ul class="form-data-list">
        <li>TourArea: {formData.TourArea}</li>
        <li>TourCount: {formData.TourCount}</li>

        <li>TourTimeMorning: {formData.TourTimeMorning ? 'Yes' : 'No'}</li>
        <li>TourTimeAfternoon: {formData.TourTimeAfternoon ? 'Yes' : 'No'}</li>
        <li>TourTimeEvening: {formData.TourTimeEvening ? 'Yes' : 'No'}</li>

        <li>Date: {formData.selectedDate ? formData.selectedDate.toLocaleDateString('he-IL', { weekday: 'long' }) + ' ' + formData.selectedDate.toLocaleDateString('he-IL') : 'No date selected'}</li>

        
        <li>Text Input: {formData.textInput}</li>
        <li>Radio Input: {formData.radioInput}</li>
        <li>Range Input: {formData.rangeInput}</li>
        <li>Free Text Input: {formData.textareaInput}</li>
        <li>Text Input 2: {formData.textInput2}</li>
      </ul>

    </div>
  );

  // ********** Buttons **********
  const handlePreviousClick = () => {
    console.log('currentPage', currentPage);
    setCurrentPage(currentPage - 1);
  };

  const handleNextClick = () => {
    console.log('currentPage', currentPage);
    setCurrentPage(currentPage + 1);
  };


  // ********** Form **********
  return (
    <div><br /><br /><br />
    <div className='Question-form-container'>
      <form className='Question-form' onSubmit={handleFormSubmit}>
        {currentPage === 0 && renderPage0()}
        {currentPage === 1 && renderPage1()}
        {currentPage === 2 && renderPage2()}
        {currentPage === 3 && renderPage3()}

          {currentPage == 0 && (
          <>
          <button className="Question-buttons-main-001" type="button" onClick={handleNextClick}>Start To Plan!</button>
          </>
          )}

          {currentPage == 1 && (
          <>
          <button className="Question-buttons-main-001" type="button" onClick={handleNextClick}>Next</button>
          </>
          )}

          {currentPage == 2 && (
          <>
          <button className="Question-buttons-main-001" type="button" onClick={handlePreviousClick}>Previous</button>
          <button className="Question-buttons-main-001" type="button" onClick={handleNextClick}>Next</button>
          </> 
          )}


          {currentPage == 3  && (
          <>
          <button className="Question-buttons-main-001" type="button" onClick={handlePreviousClick}>Previous</button>
          <button className="Question-buttons-main-001" type="submit">Submit</button>
          </>
          )}

        <br />
        <br />

      </form>
    </div>
    </div>
  );
}

