import React, { useState } from 'react';
import '../../styles//QuestionnaireForm.css';

function QuestionnaireForm({ questionnaireState, setQuestionnaireState }) {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

  const handleNextClick = () => {
    setCurrentQuestionIndex(currentQuestionIndex + 1);
  };

  const handlePreviousClick = () => {
    setCurrentQuestionIndex(currentQuestionIndex - 1);
  };

  const handleCheckboxChange = (e) => {
    const { name, checked } = e.target;
    setQuestionnaireState({
      ...questionnaireState,
      [name]: checked,
    });
  };

  const handleRadioChange = (e) => {
    const { name, value } = e.target;
    setQuestionnaireState({
      ...questionnaireState,
      [name]: value,
    });
  };

  const handleRangeChange = (e) => {
    const { name, value } = e.target;
    setQuestionnaireState({
      ...questionnaireState,
      [name]: parseInt(value),
    });
  };

  const handleTrueFalseChange = (e) => {
    const { name, value } = e.target;
    setQuestionnaireState({
      ...questionnaireState,
      [name]: value === 'true',
    });
  };
  
  const questions = [
    {
      type: 'range',
      label: 'Question One:',
      name: 'questionOne',
      min: 0,
      max: 10,
      value: questionnaireState.questionOne,
      onChange: handleRangeChange,
    },
    {
      type: 'checkbox',
      label: 'Question Two:',
      name: 'questionTwo',
      value: questionnaireState.questionTwo,
      onChange: handleCheckboxChange,
    },
    {
      type: 'select',
      label: 'Question Three:',
      name: 'questionThree',
      value: questionnaireState.questionThree,
      onChange: handleRadioChange,
      options: [
        { value: '', label: 'Select an option' },
        { value: 'optionOne', label: 'Option One' },
        { value: 'optionTwo', label: 'Option Two' },
        { value: 'optionThree', label: 'Option Three' },
      ],
    },
    {
      type: 'radio',
      label: 'Question Four:',
      name: 'questionFour',
      options: [
        { value: 'true', label: 'True' },
        { value: 'false', label: 'False' },
      ],
      value: questionnaireState.questionFour,
      onChange: handleRadioChange,
    },
    {
      type: 'textarea',
      label: 'Question Five:',
      name: 'questionFive',
      value: questionnaireState.questionFive,
      className: "textarea-qform",
      onChange: handleTrueFalseChange,
    },
  ];

  const currentQuestions = [
    questions[0],
    questions[1],
    questions[2],
    questions[3],
  ];

  const currentQuestion = currentQuestions[currentQuestionIndex];

  return (
    <div className= "div-qform">
      <div style={{ display: currentQuestionIndex === 3 ? 'none' : 'block' }}>
        <form className= "form-qform">
          <div>
            <label className= "label-qform">{currentQuestion.label}</label>
            {currentQuestion.type === 'range' && (
              <input
                className= "input-qform"
                type="range"
                name={currentQuestion.name}
                min={currentQuestion.min}
                max={currentQuestion.max}
                value={currentQuestion.value}
                onChange={currentQuestion.onChange}
            />
            )}

            {currentQuestion.type === 'checkbox' && (
            <input
             className= "input-qform"   
             type="checkbox"
             name={currentQuestion.name}
             checked={currentQuestion.value}
             onChange={currentQuestion.onChange}
            />
            )}
            
            {currentQuestion.type === 'select' && (
            <select
            name={currentQuestion.name}
            value={currentQuestion.value}
            onChange={currentQuestion.onChange}>
            
            {currentQuestion.options.map((option) => (
              <option key={option.value} value={option.value}
              >
                {option.label}
              </option>
                ))}
            </select>
            )}

            {currentQuestion.type === 'radio' && (
            <div>
                {currentQuestion.options.map((option) => (
                <label className= "label-qform" key={option.value}>
                    {option.label}
                    <input
                    className= "input-qform"
                    type="radio"
                    name={currentQuestion.name}
                    value={option.value}
                    checked={currentQuestion.value === option.value}
                    onChange={currentQuestion.onChange}
                    />
                </label>
                ))}
            </div>
            )}

            {currentQuestion.type === 'textarea' && (
            <textarea
                name={currentQuestion.name}
                value={currentQuestion.value}
                onChange={currentQuestion.onChange}
            />
            )}
        </div>
        </form>

    <div>
      {currentQuestionIndex > 0 && (
        <button className="button-qform" onClick={handlePreviousClick}>Previous</button>
      )}

      {currentQuestionIndex < currentQuestions.length - 1 && (
        <button className="button-qform" onClick={handleNextClick}>Next</button>
      )}
    </div>
  </div>

  <div style={{ display: currentQuestionIndex === 3 ? 'block' : 'none' }}>
    <p>Thank you for submitting the questionnaire!</p>
    <button className="button-qform" onClick={() => setCurrentQuestionIndex(0)}>Start Over</button>
  </div>
</div>
);
}

export default QuestionnaireForm;
