import React, { useState, useEffect } from 'react';
import { Link, history } from 'react-router-dom';
import '../../styles/NavBar.css';
import { removeJwt } from '../../helpers/auth';
import { useNavigate } from "react-router-dom";


function NavBar(props) {
  const { authToken, setAuthToken } = props;
  console.log('props:', props, 'authToken:', authToken , 'setAuthToken:' , setAuthToken);
  const navigate = useNavigate();
  

  const handleLogout = async () => {
    try {
      console.log('Current authToken:', authToken);
      const response = await fetch('/api/auth/logout', {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${authToken}`,
          'Content-Type': 'application/json',
        },
      });
      if (response.ok) {
        console.log('Logout successful!');
        setAuthToken(null);
        console.log('Updated authToken:', authToken);
        localStorage.removeItem('authToken');
        navigate(`/login`, { message: 'You have been logged out.' });
      } else {
        const errorMessage = await response.text();
        throw new Error(errorMessage);
      }
    } catch (err) {
      console.error(err);
      // Display error message to the user
      navigate(`/login`, { message: 'Logout failed. Please try again.' });
    }
  };
  
  
  
  

  return (
    <nav className="navbar">
      <ul>
        <li>
          <Link className="nav-link" to="/">
            Home
          </Link>
        </li>
        {authToken ? (
          <>
            <li>
              <Link className="nav-link" to="/personal-area">
                Personal area
              </Link>
            </li>
            <li>
              <Link className="nav-link" onClick={handleLogout}>
                Log out
              </Link>
            </li>
          </>
        ) : (
          <>
            <li>
              <Link className="nav-link" to="/login">
                Log In
              </Link>
            </li>
            <li>
              <Link className="nav-link" to="/signup">
                Sign up
              </Link>
            </li>
          </>
        )}
      </ul>
    </nav>
  );
}

export default NavBar;
