import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../../styles/UserForm.css';

const SignUp = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState(''); // add message state
  const navigate = useNavigate();

  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
  };

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!username) {
      setMessage('Please enter a username.');
      return;
    }
    try {
      const usernameResponse = await axios.get(`/api/check-username?username=${username}`);
      if (!usernameResponse.data.available) {
        setMessage('Username already exists. Please choose another one.');
        return;
      }
      const response = await axios.post('/api/signup', {
        username,
        email,
        password,
      });
      localStorage.setItem('user-name', username);
      console.log('user-name', username);
      console.log(response.data);
      setMessage('SUCCESS!'); // update message state with success message
      navigate('/login');
    } catch (error) {
      console.log(error.response.data);
      setMessage(error.response.data); // update message state with error message
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <br /><br /><br /><br />
      <label>
        Username:<br />
        <input type="text" value={username} onChange={handleUsernameChange} />
      </label>
      <label>
        Email:<br />
        <input type="email" value={email} onChange={handleEmailChange} />
      </label>
      <label>
        Password:<br />
        <input type="password" value={password} onChange={handlePasswordChange} />
      </label>
      <button type="submit">Sign Up</button>
      {message && <p className={message.includes('SUCCESS') ? 'success' : 'error'}>{message}</p>}
      {/* display message if it exists */}
    </form>
  );
};

export default SignUp;
