import React, { useState } from 'react';
import { setJwt } from '../../helpers/auth';
import axios from 'axios';
import '../../styles/UserForm.css';
import { useNavigate } from 'react-router-dom';
import NavBar from '../../common/components/NavBar';

function LogIn(props) {
   const [email, setEmail] = useState('');
   const [password, setPassword] = useState('');
   const [error, setError] = useState('');
   const navigate = useNavigate();

   const handleSetAuthToken = (token) => {
     props.setAuthToken(token);
   };

   const handleSubmit = async (event) => {
     event.preventDefault();
     try {
       const response = await axios.post('/api/login', { email, password });
       const { token } = response.data;
       setJwt(token);
       localStorage.setItem('authToken', token);
       handleSetAuthToken(token);
       navigate(`/PersonalZone/${token}`);
       console.log('Log-In Success');
     } catch (error) {
       console.error(error);
       setError('Incorrect email or password');
     }
   };
  
  

   return (
     <>
       <NavBar authToken={props.authToken} setAuthToken={handleSetAuthToken} />
       <div className="login-form-container">
         <form className="login-form" onSubmit={handleSubmit}><br /><br /><br /><br />
           <div className="form-input">
             <label htmlFor="email">Email:</label>
             <input
               type="email"
               name="email"
               placeholder="Enter email"
               required
               onChange={(event) => setEmail(event.target.value)}
             />
           </div>
           <div className="form-input">
             <label htmlFor="password">Password:</label>
             <input
               type="password"
               name="password"
               placeholder="Enter password"
               required
               onChange={(event) => setPassword(event.target.value)}
             />
           </div>
           {error && <div className="error-message">{error}</div>}
           <button type="submit">Log In</button>

         </form>
       </div>
     </>
   );
}

export default LogIn;
