import React, { useState, useEffect } from 'react';
// import '../../styles//QuestionnaireForm.css';

function MultiPageForm() {
  const [formData, setFormData] = useState({
    TourArea: '',
    TourCount: '',
    TourTime: '',

    textInput: '',
    checkboxInput: false,
    radioInput: '',
    rangeInput: '',
    textareaInput: '',
    textInput2: '',
  });

  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(3);

  useEffect(() => {
    // Save form data to local storage whenever formData changes
    localStorage.setItem('formData', JSON.stringify(formData));
  }, [formData]);

  useEffect(() => {
    // Load form data from local storage when component mounts
    const storedData = localStorage.getItem('formData');
    if (storedData) {
      setFormData(JSON.parse(storedData));
    }
  }, []);

  const handleInputChange = (event) => {
    const { name, value, type, checked } = event.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  const handleFormSubmit = (event) => {
    console.log('currentPage', currentPage);
    event.preventDefault();
    // Submit form data to server or do other final processing
    // ...
    alert('Form submitted successfully!');
  };

  const renderPage1 = () => (
    <div className="form-page">
      <h2>Page 1 of {totalPages}</h2>
      <label>
        :בחרו איזור לסיור היין שלכם
        <select
          name="TourArea"
          value={formData.TourArea}
          onChange={handleInputChange}
        >
          <option value="">יש לבחור מהרשימה</option>
          <option value="Jerusalem">ירושלים</option>
          <option value="Center">מרכז</option>
          <option value="North">צפון</option>
        </select>
      </label>

      <br /><br />

      <label>
        :בכמה יקבים תרצו לסייר בסיור היין שלכם
        <select
          name="TourCount"
          value={formData.TourCount}
          onChange={handleInputChange}
        >
          <option value="">יש לבחור מהרשימה</option>
          <option value="1">1</option>
          <option value="2">2</option>
          <option value="3">3</option>
        </select>
      </label>

      <br /><br />

      <label>
        :מהן שעות הסיור הרצויות עבורכם
        <select
          name="TourTime"
          value={formData.TourTime}
          onChange={handleInputChange}
        >
          <option value="">יש לבחור מהרשימה</option>
          <option value="Morning">בוקר</option>
          <option value="Afternoon">צהריים</option>
          <option value="Evening">ערב</option>
        </select>
      </label>

      {/* <label>
        Text Input:
        <input
          type="text"
          name="textInput"
          value={formData.textInput}
          onChange={handleInputChange}
        />
      </label>
      <br />
      <label>
        Checkbox Input:
        <input
          type="checkbox"
          name="checkboxInput"
          checked={formData.checkboxInput}
          onChange={handleInputChange}
        />
      </label>
      <br />
      <label>
        Radio Input:
        <br />
        <label>
          Option 1
          <input
            type="radio"
            name="radioInput"
            value="option1"
            checked={formData.radioInput === 'option1'}
            onChange={handleInputChange}
          />
        </label>
        <br />
        <label>
          Option 2
          <input
            type="radio"
            name="radioInput"
            value="option2"
            checked={formData.radioInput === 'option2'}
            onChange={handleInputChange}
          />
        </label>
      </label>
      <br />
      <label>
        Select Input:
        <select
          name="selectInput"
          value={formData.selectInput}
          onChange={handleInputChange}
        >
          <option value="">יש לבחור איזור</option>
          <option value="Jerusalem">ירושלים</option>
          <option value="Center">מרכז</option>
          <option value="North">צפון</option>
        </select>
      </label> */}
    </div>
  );

  const renderPage2 = () => (
    <div className="form-page">
      <h2>Page 2 of {totalPages}</h2>
      <label>
        Range Input:
        <input
          type="range"
          name="rangeInput"
          min="0"
          max="100"
          value={formData.rangeInput}
          onChange={handleInputChange}
        />
        <span>{formData.rangeInput}</span>
      </label>
      <br />
      <label>
        Free Text Input:
        <textarea
          name="textareaInput"
          value={formData.textareaInput}
          onChange={handleInputChange}
        />
      </label>
      <br />
      <label>
        Text Input 2:
        <input
          type="text"
          name="textInput2"
          value={formData.textInput2}
          onChange={handleInputChange}
        />
      </label>
    </div>
  );

  const renderPage3 = () => (
    <div className="form-page">
      <h2>Page 3 of {totalPages}</h2>
      <p>Summary of form data:</p>
      <ul>
        <li>TourArea: {formData.TourArea}</li>
        <li>TourCount: {formData.TourCount}</li>


        <li>Text Input: {formData.textInput}</li>
        <li>Checkbox Input: {formData.checkboxInput ? 'Yes' : 'No'}</li>
        <li>Radio Input: {formData.radioInput}</li>
        <li>Range Input: {formData.rangeInput}</li>
        <li>Free Text Input: {formData.textareaInput}</li>
        <li>Text Input 2: {formData.textInput2}</li>
      </ul>
    </div>
  );

  const handlePreviousClick = () => {
    console.log('currentPage', currentPage);
    setCurrentPage(currentPage - 1);
  };

  const handleNextClick = () => {
    console.log('currentPage', currentPage);
    setCurrentPage(currentPage + 1);
  };

  return (
    <form onSubmit={handleFormSubmit}>
      {currentPage === 1 && renderPage1()}
      {currentPage === 2 && renderPage2()}
      {currentPage === 3 && renderPage3()}

      <div className="form-buttons">
      {currentPage == 1 && (
        <div>
          <button type="button" onClick={handleNextClick}>Next</button>
        </div>
        )}
      {currentPage == 2 && (
        <div>
          <button type="button" onClick={handleNextClick}>Next</button>
          <button type="button" onClick={handlePreviousClick}>Previous</button>
        </div>
        )}
        {currentPage == 3  && (
        <div>
        <button type="button" onClick={handlePreviousClick}>Previous</button>
        <button type="submit">Submit</button>
        </div>
        )}
      </div>
    </form>
  );
}

export default MultiPageForm;
        
