import React, { useState } from 'react';

function QuestionnaireForm({ questionnaireState, setQuestionnaireState }) {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

  const handleNextClick = () => {
    setCurrentQuestionIndex(currentQuestionIndex + 1);
  };

  const handlePreviousClick = () => {
    setCurrentQuestionIndex(currentQuestionIndex - 1);
  };


  const handleCheckboxChange = (e) => {
    const { name, checked } = e.target;
    setQuestionnaireState({
      ...questionnaireState,
      [name]: checked,
    });
  };

  const handleRadioChange = (e) => {
    const { name, value } = e.target;
    setQuestionnaireState({
      ...questionnaireState,
      [name]: value,
    });
  };

  const handleRangeChange = (e) => {
    const { name, value } = e.target;
    setQuestionnaireState({
      ...questionnaireState,
      [name]: parseInt(value),
    });
  };

  const handleTrueFalseChange = (e) => {
    const { name, value } = e.target;
    setQuestionnaireState({
      ...questionnaireState,
      [name]: value === 'true',
    });
  };

  const questions = [
    {
      type: 'range',
      label: 'Question One:',
      name: 'questionOne',
      min: 0,
      max: 10,
      value: questionnaireState.questionOne,
      onChange: handleRangeChange,
    },
     

    {
      type: 'checkbox',
      label: 'Question Two:',
      name: 'questionTwo',
      value: questionnaireState.questionTwo,
      onChange: handleCheckboxChange,
    },
    {
      type: 'select',
      label: 'Question Three:',
      name: 'questionThree',
      value: questionnaireState.questionThree,
      onChange: handleRadioChange,
      options: [
        { value: '', label: 'Select an option' },
        { value: 'optionOne', label: 'Option One' },
        { value: 'optionTwo', label: 'Option Two' },
        { value: 'optionThree', label: 'Option Three' },
      ],
    },
    {
      type: 'radio',
      label: 'Question Four:',
      name: 'questionFour',
      options: [
        { value: 'true', label: 'True' },
        { value: 'false', label: 'False' },
      ],
      value: questionnaireState.questionFour,
      onChange: handleRadioChange,
    },
    {
      type: 'textarea',
      label: 'Question Five:',
      name: 'questionFive',
      value: questionnaireState.questionFive,
      onChange: handleTrueFalseChange,
    },
  ];

  const currentQuestion = questions[currentQuestionIndex];

  return (
    <iframe src="/questionnaire.html" style={{ width: '100%', height: '500px' }} />
  );
}

export default QuestionnaireForm;
